import React, { Component } from 'react';
import CardList from './CardList';
import {robots} from './robots';
import SearchBox from './SearchBox'


class App extends Component {
    constructor(){
        this.state = {
            robots: robots,
            searchfield: ''
        }
    }
    render(){
        return(
            <div className='tc'>
                <h1>RoboFriends</h1>
                <SearchBox />
                <CardList robots = {robots}/>
            </div>
        );
    }

}

export default App;
